import React from "react";
import backgroundImage from "../../public/images/IMG_0305.jpg";

const Home = () => {
  const scrollToNextSection = () => {
    const nextSection = document.getElementById("about");
    if (nextSection) {
      nextSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <header
      className="home-section"
      id="home"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        color: "white",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
      }}
    >
      <div className="overlay-content">
        <h1 className="main-title">I'm Nishant Sharma.</h1>
        <p className="subtitle">Explorer and Web Developer.</p>
        <button className="scroll-btn" onClick={scrollToNextSection}>
          â¬‡ Scroll Down
        </button>
      </div>
    </header>
  );
};

export default Home;
