import React from "react";

const Home = () => {
  const scrollToNextSection = () => {
    const nextSection = document.getElementById("about"); // Update to your next section's ID
    if (nextSection) {
      nextSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <header
      id="home"
      className="w3-container w3-center w3-black"
      style={{
        backgroundImage: "url('/images/IMG_0305.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        color: "white",
      }}
    >
      <h1 className="w3-jumbo">
        <span className="w3-hide-small">I'm</span> Nishant Sharma.
      </h1>
      <p>Explorer and Web Developer.</p>

      {/* Scroll Button */}
      <button
        onClick={scrollToNextSection}
        className="w3-button w3-white w3-margin-top"
        style={{ marginTop: "2rem" }}
      >
        Scroll Down
      </button>
    </header>
  );
};

export default Home;
