import React from "react";

const Home = () => {
  const scrollToNextSection = () => {
    const nextSection = document.getElementById("about");
    if (nextSection) {
      nextSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section
      id="home"
      style={{
        backgroundImage: `url("/images/IMG_0305.jpg")`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        height: "100vh",
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
        textAlign: "center",
      }}
    >
      <h1 style={{ fontSize: "3rem", marginBottom: "1rem" }}>I'm Nishant Sharma.</h1>
      <p style={{ fontSize: "1.5rem" }}>Explorer and Web Developer.</p>
      <button
        onClick={scrollToNextSection}
        style={{
          marginTop: "2rem",
          padding: "10px 20px",
          backgroundColor: "white",
          color: "black",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          fontWeight: "bold",
        }}
      >
        â¬‡ Scroll Down
      </button>
    </section>
  );
};

export default Home;
