import "./"; // We'll define background styling here

const Home = () => {
  const scrollToNextSection = () => {
    const nextSection = document.getElementById("about");
    if (nextSection) {
      nextSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <header className="home-section" id="home">
      <div className="overlay-content">
        <h1 className="main-title">I'm Nishant Sharma.</h1>
        <p className="subtitle">Explorer and Web Developer.</p>
        <button className="scroll-btn" onClick={scrollToNextSection}>
          â¬‡ Scroll Down
        </button>
      </div>
    </header>
  );
};

export default Home;
